function redirectToRoom(){null==selectedRoom&&(selectedRoom=document.getElementById("rooms").rows[1].id);location.href="/rooms/"+selectedRoom}function searchRoom(){for(var a=document.getElementById("rooms").getElementsByTagName("tbody")[0].rows,c=document.getElementById("room-search"),b=0;b<a.length;b++)a[b].className="",""!=c.value&&-1==a[b].cells[0].innerHTML.indexOf(c.value)&&(a[b].className="invisible")}
