function convertTime(a){var c="now";1===a?c="second":1<a&&60>a?c="seconds":60<=a&&120>a?(a/=60,c="minute"):120<=a&&3600>a?(a/=60,c="minutes"):3600<=a&&7200>a?(a=a/60/60,c="hour"):7200<=a&&86400>a?(a=a/60/60,c="hours"):86400<=a&&172800>a?(a=a/60/60/24,c="day"):172800<=a&&(a=a/60/60/24,c="days");a=Math.floor(a);return"now"===c?c:a+" "+c};
